/**
 * <p>
 * 
 * </p>
 *
 * @author wangruiyu
 * @since 2020/8/17
 */
class TicketWindows {
    private int count;

    public TicketWindows(int count) {
        this.count = count;
    }

    public int getCount() {
        return count;
    }

    public int sell(int amount) {

        if (count >= amount) {
            synchronized (this) {
                count -= amount;
            }
            return amount;
        }
        return 0;

    }
}

