    public Account(int money) {
        this.money = money;
    }

    public int getMoney() {
        return money;
    }

    public void setMoney(int money) {
        this.money = money;
    }

    /**
     * 
     *
     * @param account 
     * @param money   
     */
    public void transfer(Account account, int money) {
        synchronized (Account.class) {
            if (this.money >= money) {
                account.setMoney(account.getMoney() + money);
                this.setMoney(this.getMoney() - money);
            }
        }
