package main

import (
	"fmt"
	"github.com/vmihailenco/msgpack"
)

type Person struct {
	Name string
	Age  int
}

func main() {
	p := Person{Name: "Alice", Age: 30}
	msgpackData, err := msgpack.Marshal(p)
	if err != nil {
		fmt.Println("Error:", err)
		return
	}
	fmt.Println(msgpackData) // 

	var p2 Person
	err = msgpack.Unmarshal(msgpackData, &p2)
	if err != nil {
		fmt.Println("Error:", err)
		return
	}
	fmt.Println(p2) // : {Alice 30}
}
```

